import { useState } from "react";
import { useEffect } from "react";
export default function SearchUI({ setSearchTerm }) {

    const [newSearchTerm, setNewSearchTerm] = useState("")
    const [breeds, setBreeds] = useState([])
    function handleSubmit(event) {
        event.preventDefault();
        setNewSearchTerm("");
        setSearchTerm(newSearchTerm);

    }

    fetch(`https://api.thecatapi.com/v1/breeds`)
    .then( (response) => response.json() )
    .then( (breeds) => {setBreeds(breeds)        
    })
    return (
        <>
        <form onSubmit={handleSubmit}>
        <input type="text" value={newSearchTerm} onChange={ (event) => setNewSearchTerm(event.target.value)} placeholder="Search cats..." id="name"></input>
        <button type="submit">Find that cat</button>
        <select name="breeds">
        {breeds.map( (breed) => <option key={breeds[breed].id} value={breeds[breed].name}>breeds[breed].name</option>)}
        </select>
        </form>
        </>
    );
}
