import './App.css';
import Title from './components/Title'
import CatCardList from './components/CatCardList'
import SearchUI from './components/SearchUI';
import { useEffect, useState } from 'react';
import BreedFactsCard from './components/BreedFactsCard';
const defaultBreed = 
function App() {
  const [searchTerm, setSearchTerm] = useState({})
  const [breeds, setBreeds] = useState([])
useEffect(() =>{
  fetch(`https://api.thecatapi.com/v1/breeds`)
  .then( (response) => response.json() )
  .then( (breeds) => {
      setBreeds(breeds)   
  })
},[])


  return (
    <div className="App">
   <Title/>
   <SearchUI setSearchTerm={setSearchTerm} breeds={breeds} />
   <CatCardList searchTerm={searchTerm} />
   <BreedFactsCard searchTerm={searchTerm} ></BreedFactsCard>
    </div>
  );
}

export default App;
